"use strict";

class Runner {
  constructor(config) {
    this.tagArgs = "";
    this.userArgs = "";
    this.config = config;
  }
  setUserArgs(userArgs) {
    this.userArgs = userArgs;
  }
  setTagArgs(tagArgs) {
    this.tagArgs = tagArgs;
  }
  setConfig(config) {
    this.config = config;
  }
  run() {
    throw new Error("Method 'run()' must be implemented.");
  }
}
module.exports = Runner;